@implementation MakeData

+ (id)shareInstance{
    static MakeData *makeData = nil;
    static dispatch_once_t predicate;
    dispatch_once(&predicate, ^{
        makeData = [[MakeData alloc] init];
    });
    return makeData;
}

- (void)createData{
    [[MakeData shareInstance] createDynastyList];
    [[MakeData shareInstance] createStoreList];
    [[MakeData shareInstance] createStore];
    [[MakeData shareInstance] createPersonList];
    [[MakeData shareInstance] createPersonDetail];
}

